import React, { Component } from 'react'
// import '../css/phone.css'
import Logo from './logo/Logo';

class PhoneNumber extends Component {
    nextPage(e) {
        e.preventDefault();
        var that = this
        that.props.props.history.push('/phoneNumberSMS') // Переход на страницы  phoneNumberSMS
    }
    render() {
        return (
            <div className='forform'>
                <Logo />
                    <span><strong>Восстановление пароля</strong></span>
                    <form onSubmit={this.nextPage.bind(this)} id="phoneNumber" >
                        <input name="" type="tel" title="375 29 123 45 67"
                            pattern="^([\+]?375|(8[\s|\-]?0))[\s|\-]?(29|33|25|44|33|17)[\s|\-]?([0-9]{3}[\s|\-]?[0-9]{2}[\s|\-]?[0-9]{2}[\s|\-]?)$" required className="button-my button-big-my button-gray"
                            placeholder="+375 __ ___ __ __" />
                    </form>

                        <button type="submit" className="button-my button-big-my button-red" form="phoneNumber">Далее </button>


            </div>
        )
    }
}
export default PhoneNumber
