import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import '../css/passwordrecovery.css'

class PasswordRecoveryMid extends Component {
    render() {
        return (
            <div>
                <div className="btn-row-my">
                    <Link to={{ pathname: '/phoneNumber' }} className="colorWhite button-my button-big-my button-red">*Отправить по SMS</Link>
                </div>
                <div className="textUndrerJT marginTopZero" ><span className="colorOrange">*</span><span>На указанный номер мобильного при регистрации</span></div>
                <div className="btn-row-my">
                    <Link to={{ pathname: '/email' }} className="colorWhite button-my button-big-my button-red">**На E-mail</Link>
                </div>
                <div className="textUndrerJT marginTopZero"><span className="colorOrange">**</span><span>Восстановление через указанный при регистрации E-mail</span></div>
            </div>
        )
    }
}
export default PasswordRecoveryMid
