import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Logo from './logo/Logo'
import '../css/passwordrecovery.css'

class PasswordRecovery extends Component {
    render() {
        return (
            <div className="wrapper">
                <Logo />
                <span className="title"><strong>Выберите способ восстановления пароля</strong></span>
                <Link to={{ pathname: '/phoneNumber' }} className="colorWhite button-my button-big-my button-red">*Отправить по SMS</Link>
                <div><span className="colorOrange title">*</span><span>На указанный номер мобильного при регистрации</span></div>
                <Link to={{ pathname: '/email' }} className="colorWhite button-my button-big-my button-red">**На E-mail</Link>
                <div><span className="colorOrange title">**</span><span>Восстановление через указанный при регистрации E-mail</span></div>
            </div>
        )
    }
}
export default PasswordRecovery
