import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux';
import '../css/allmystyle.css'

class RegSMSMid extends Component {

    constructor() {
        super()
        this.state = {

        }
    }
    nextPage(e) {
        debugger
        e.preventDefault();
        var that = this
        if(this.props.store.legalOrPrivatePath !== null){ //если null - активный юзер - не физ и не юр, а диспетчер
            that.props.props.history.push(this.props.store.legalOrPrivatePath) // Переход на страницы 
        }else{
        that.props.props.history.push(this.props.next)
        }
    }
    render() {
        return (
            <div className="margin-top-45 forform">
                <form onSubmit={this.nextPage.bind(this)} id="test">
                    <div className="button-big-my">
                       <input className="button-my button-big-my button-gray" defaultValue={this.props.store.user.sms_code} type="text" required placeholder="Введите код полученный в SMS"/>
                    </div>
                </form>
                <div className="button-big-my">
                    <div className="btn-row-my ">
                        <Link to={{ pathname: 'regOne' }} className="colorWhite button-my button-small-my button-red marg-1" ><strong>Назад</strong></Link>
                        <button type="submit" className="button-my button-small-my button-red marg-1 " form="test">Далее</button>
                    </div>
                </div>
            </div>
        )
    }
}
const mapStateToProps = function (state) {
    return {
        // из раздела Redux с именем counter свойство cnt будет доступно
        // данному компоненту как this.props.cnt
        store: state.store,
    };
};

export default connect(mapStateToProps)(RegSMSMid);
