import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux';
import { counterButton_create } from "../redux/countersAC";
import Logo from './logo/Logo'
import eye from '../images/red-eye.png'
import GoogleLogin from 'react-google-login';
// import FacebookLogin from 'react-facebook-login';
import FacebookLogin from 'react-facebook-login/dist/facebook-login-render-props'
import '../css/allmystyle.css'

class Authorization extends Component {
    constructor() {
        super()
        this.state = {
            typePassword: "password",
            typePassword2: "password",
            pathLink: ''
            // messegeError:"Вы ввели не верно Логин или Пароль папробуйте снова или восстановите его!",
        }
    }
    onClickEye(e) {
        let name = e.currentTarget.name;
        if (name === "eye1") {
            let item = this.state.typePassword === "password" ? "text" : "password";
            this.setState({ typePassword: item });
        } else {
            let item = this.state.typePassword2 === "password" ? "text" : "password";
            this.setState({ typePassword2: item });
        }
        this.props.dispatch(counterButton_create(this.state));
        // console.log(this.props)
    }
    onSuccess() {
        // console.log("Всё Ок")
    }
    onFailure() {
        // console.log("Сука Лох")
    }
    nextPage(e) {
        e.preventDefault();
        var that = this
        var url = 'http://178.159.45.189/api/rest-auth/login/'
        var formElem = document.getElementById('authorization')
        var formData = new FormData(formElem)
        var user = {}

        for (var [key, value] of formData.entries()) {
            if (value[0] == '+') {
                user.phone = value
            } else if (key == 'password') {
                user.password = value
            } else {
                user.email = value
            }
        }
        fetch(url, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(user),
        }).then(
            function (response) {
                if (response.status !== 200) {
                    console.log('Looks like there was a problem. Status Code: ' +
                        response.status);
                }
                return response.json();
            })
            .then(function (data) {
                that.props.dispatch(counterButton_create(data));
                let nextLocation = data.user.user_type == 3 ? '/admin/activeOrders' : '/customer/createOrder/selectdate';
                that.props.history.push(nextLocation) // Переход на страницы админ/пользователь
            })
    }

    render() {
        return (
            <div className="wrapper">
                <div className="centr-page">
                    <Logo />
                    <div className="">
                        <form onSubmit={this.nextPage.bind(this)} id="authorization" >
                            <div className="div-col-my">
                                <label className="textRedbig margin-bot-20">{this.state.messegeError}</label>
                                <div className="div-password-my button-big-my button-gray">
                                    <input name="phoneEmail" type="text" className=" button-gray too textNormal-my inp-my div-password-my"
                                        placeholder="Введите номер телефона или E-mail"
                                        title="name@mail.ru 375 29 123 45 67" required
                                        pattern="(^([\+]?375|(8[\s|\-]?0))[\s|\-]?(29|33|25|44|33|17)[\s|\-]?([0-9]{3}[\s|\-]?[0-9]{2}[\s|\-]?[0-9]{2})$)|(^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$)" />
                                </div>
                                <div className="div-password-my button-big-my button-gray">
                                    <input name="password" id="password2" type={this.state.typePassword} required className="inputin-my button-password-my button-normal-my button-gray too textNormall"
                                        placeholder="Введите пароль"
                                        title="код неменее 8-х символов" pattern="^.{8,40}" />
                                    <img className="cross eyeq-my" name="eye1" src={eye} alt="eye" onClick={this.onClickEye.bind(this)} />
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                <div className="">
                    <div className=" fortext" >
                        <span className="textRedbig cross margin-bot-20"><Link to={{ pathname: '/passwordRecovery' }} className="textRedbig">Забыли пароль?</Link></span><br />
                        <input className="margin-5" type="checkbox" name="" value="" id="savePass" /><label htmlFor="savePass"><strong>Запомнить / Сохранить пароль</strong></label>
                    </div>
                    <div>
                        <div className="div-col-my">
                            < GoogleLogin
                                clientId="xxx.apps.googleusercontent.com "
                                buttonText="Login"
                                onSuccess={this.onSuccess}
                                onFailure={this.onFailure}
                                render={renderProps => { return <button onClick={renderProps.onClick} className="button-my button-big-my button-red">Войти с помощью Google</button> }} />

                            <FacebookLogin
                                appId="1088597931155576"
                                autoLoad={true}
                                fields="name,email,picture"
                                callback={this.onFailure}
                                render={renderProps => { return <button onClick={renderProps.onClick} className="button-my button-big-my button-red" >Войти с помощью Facebook</button> }}
                            />

                        </div>
                    </div>
                    <div >

                        <div className=" ">
                            <Link to={{ pathname: '/' }} className="colorWhite button-my button-small-my button-red marg-1">Назад</Link>
                            <button type="submit" className="button-my button-small-my button-red marg-1" form="authorization" >Войти</button>
                        </div>
                    </div>
                    <Link to={{ pathname: 'privacyPolicy' }}>
                        <div className="textRedSmall margin-top-15"><span className="cross"><br />Политика конфиденциальности <br />и условия использования</span></div>
                    </Link>
                </div>
            </div>
        )
    }
}
const mapStateToProps = function (state) {
    // console.log(state.counter, "Authorization")
    return {
        // из раздела Redux с именем counter свойство cnt будет доступно
        // данному компоненту как this.props.cnt
        cnt: state.store.cnt,
    };
};

// присоединяем (connect) компонент к хранилищу Redux
export default connect(mapStateToProps)(Authorization);
