import React, { Component } from 'react'
import { Link } from 'react-router-dom'

export default class PopupOrderIsAccepted extends Component {

	onCloseClick = () => {
		const popup =  document.getElementsByClassName('cancel')[0].parentNode;
        popup.setAttribute('class', 'container close');
	}

	render() {

		const { name, number_of_order } = this.props;

		return (
				<div className="popups order-is-accepted disablePopup">
					<h3>Заказ принят!</h3>
					<p className="info">
						Статус заказа №{ number_of_order } изменён на "Заказ принят".
					</p>
					<p className="choose-driver">
						<strong>Выбор водителя:</strong>
					</p>
					<select className="for-input for-input-width95" name="equipmentName" id="">
						<option value="1" default>Иванов Иван Иванович</option>
					</select>
					<Link to={{ pathname: ''}}><button type="button" className="btn refuse">Отправить уведомление водителю</button></Link>
				</div>
		)
	}
}
