import React, { Component } from 'react'
import { connect } from 'react-redux';
import { counterButton_create } from "../redux/countersAC";
import { Link } from 'react-router-dom'
import eye from '../images/red-eye.png'
import '../css/allmystyle.css'

class RegOneMid extends Component {
    constructor() {
        super()
        this.state = {
            typePassword: "password",
            typePassword2: "password",
            password2: "",
            location: null,
        }
    }
    onClickEye(e) {
        let name = e.currentTarget.name;
        if (name === "eye1") {
            let item = this.state.typePassword === "password" ? "text" : "password";
            this.setState({ typePassword: item });
        } else {
            let item = this.state.typePassword2 === "password" ? "text" : "password";
            this.setState({ typePassword2: item });
        }
    }
    nextPage(e) {
        //debugger
        e.preventDefault();
        var that = this
        var urlFiz = "http://178.159.45.189/api/rest-auth/registration/client/private/"
        var urlJyr = "http://178.159.45.189/api/rest-auth/registration/client/legal/"
        var urlDispatcher = "http://178.159.45.189/api/rest-auth/registration/dispatcher/"
        var url = (this.props.history.location.pathname === "/contractor/regOne") ? urlDispatcher : urlFiz = (this.props.store.legalPrivate) ? urlFiz :urlJyr;
        var formElem = document.getElementById('regOne')
        fetch(url, {
            method: "POST",
            headers: {
                // "Content-Type": "application/json",
            },
            body: new FormData(formElem),
        }).then(
            function (response) {
                // if (response.status !== 201) {
                //     console.log('Looks like there was a problem. Status Code: ' +
                //         response.status);
                // }
                // console.log(response)
                return response.json();
            })
            .then(function (data) {
                that.props.dispatch(counterButton_create(data));
                console.log(data);
                that.props.history.push('regSMS') // Переход на страницы админ/пользователь 
            })
            .catch(function (err) {
                console.log('Fetch Error :-S', err);
            });
    }

    password(e) {
        let value = e.currentTarget.value;
        this.setState({ password2: value });
    }

    render() {
        return (
            <div className="forform">
                <div className="centr-page">
                    <div>
                        <div className="textUndrerJT"><span><strong>Регистрация</strong></span></div>
                        <form onSubmit={this.nextPage.bind(this)} className="form div-col-my" id="regOne">
                            <div className="div-password-my button-big-my button-gray">
                                <input className="too inp-my" name="email" type="text" title="name@mail.ru" required pattern="^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$" placeholder="*Введите E-mail" />
                            </div>
                            <input name="phone" type="tel" title="375 29 123 45 67"
                                pattern="^([\+]?375|(8[\s|\-]?0))[\s|\-]?(29|33|25|44|33|17)[\s|\-]?([0-9]{3}[\s|\-]?[0-9]{2}[\s|\-]?[0-9]{2})$" required className="button-my button-big-my button-gray"
                                placeholder="+375 __ ___ __ __" />
                            <div className="div-password-my button-big-my button-gray">
                                <input name="password1" id="password2" type={this.state.typePassword} required className="inputin-my button-password-my  button-normal-my button-gray too"
                                    placeholder="Придумайте новый пароль"
                                    title="Пороль неменее 8-х символов" pattern="^.{8,40}" onChange={this.password.bind(this)} />
                                <img className="cross" name="eye1" src={eye} alt="eye" onClick={this.onClickEye.bind(this)} />
                            </div>
                            <div className="div-password-my button-big-my button-gray">
                                <input name="password2" title="Пороли не совподает" id="password-check2" pattern={this.state.password2} type={this.state.typePassword2} required className="inputin-my button-password-my button-normal-my button-gray too"
                                    placeholder="Подтвердите пароль" />
                                <img className="cross" name="eye2" src={eye} alt="eye" onClick={this.onClickEye.bind(this)} />
                            </div>
                        </form>
                        <div className="textUndrerJT marginTopZero" ><span className="colorOrange"> * </span><span> Проверьте корректность ввода e-mail.<br />  На указанный email в процессе работы<br /> будет высылаться необходимая <br />документация.</span></div>
                    </div>

                    <div className="btn-row-my">
                        <Link to={{ pathname: '/selectRole' }} className='colorWhite button-my button-small-my button-red marg-1'><strong>Назад</strong></Link>
                        <button type="submit" className="button-my button-small-my button-red marg-1" form="regOne">Далее</button>
                    </div>
                </div>

            </div>
        )
    }
}
const mapStateToProps = function (state) {
    console.log(state)
    return {
        // из раздела Redux с именем counter свойство cnt будет доступно
        // данному компоненту как this.props.cnt
        store: state.store,
    };
};

export default connect(mapStateToProps)(RegOneMid);
